import { v4 as uuidv4 } from 'uuid'
import { GET_INSTRUMENTS, ADD_INSTRUMENT } from '../../queries'
const { Option } = Select;
const handleChange = (value) =>{
    console.log(`selected ${value}`);

}

const AddInstrument = () => {
